		if(state == 101 && parceiro_un == '' && substatus != 201){
			check = true;
		}
		else if(state == 101 && parceiro_un !='' && gs.getUser().isMemberOf('fb774fe8dba06f00530ef33eae961991')){
			check = true;
		}
		else if(state == 105 && substatus != 201 && parceiro_un == ''){
			check = true;
