		else if(state == 106 && gs.getUser().isMemberOf('fb774fe8dba06f00530ef33eae961991') && parceiro_un != ''){
			check = true;
		}
		else if(state == 104 && substatus == '' ){
			check = true;
		}

		return check;
	},

