		var check = false;
		if(state == 101 && (parceiro_un == '' || parceiro_un == "NULL") && substatus != 201){
			check = true;
		}
		else if(state == 105 && (parceiro_un == '' || parceiro_un == "NULL")&& substatus !=201){
			check = true;
		}
		else if(state == 105 && parceiro_un !='' && gs.getUser().isMemberOf(' fb774fe8dba06f00530ef33eae961991')){
			check = true;
		}
		else if(state == 106 && (chg == '' || chg == "NULL") && substatus != 201 && (parceiro_un == '' || parceiro_un == "NULL")){
			check = true;
		}
		else if(state == 106 && parceiro_un !='' && gs.getUser().isMemberOf('fb774fe8dba06f00530ef33eae961991')){
			check = true;
		}

		return check;
	},

